yarn.nodemanager.local-dirs={{nm_local_dirs}}
yarn.nodemanager.log-dirs={{nm_log_dirs}}
yarn.nodemanager.linux-container-executor.group={{yarn_executor_container_group}}
banned.users=hdfs,yarn,mapred,bin
min.user.id={{min_user_id}}

{{ '[docker]' }}
  module.enabled={{docker_module_enabled}}
  docker.binary={{docker_binary}}
  docker.allowed.capabilities={{docker_allowed_capabilities}}
  docker.allowed.devices={{docker_allowed_devices}}
  docker.allowed.networks={{docker_allowed_networks}}
  docker.allowed.ro-mounts={{nm_local_dirs}},{{docker_allowed_ro_mounts}}
  docker.allowed.rw-mounts={{nm_local_dirs}},{{nm_log_dirs}},{{docker_allowed_rw_mounts}}
  docker.privileged-containers.enabled={{docker_privileged_containers_enabled}}
  docker.trusted.registries={{docker_trusted_registries}}
  docker.allowed.volume-drivers={{docker_allowed_volume_drivers}}

{{ '[gpu]' }}
  module.enabled={{gpu_module_enabled}}

{{ '[cgroups]' }}
  root={{cgroup_root}}
  yarn-hierarchy={{yarn_hierarchy}}

# export JAVA_HOME=/home/y/libexec/jdk1.8.0/

export HADOOP_JOB_HISTORYSERVER_HEAPSIZE={{jobhistory_heapsize}}

# We need to add the RFA appender for the mr daemons only;
# however, HADOOP_MAPRED_LOGGER is shared by the mapred client and the
# daemons. This will restrict the RFA appender to daemons only.
export HADOOP_LOGLEVEL=${HADOOP_LOGLEVEL:-INFO}
export HADOOP_ROOT_LOGGER=${HADOOP_ROOT_LOGGER:-INFO,console}
export HADOOP_DAEMON_ROOT_LOGGER=${HADOOP_DAEMON_ROOT_LOGGER:-${HADOOP_LOGLEVEL},RFA}

{% if security_enabled %}
export MAPRED_HISTORYSERVER_OPTS="-Djava.security.auth.login.config={{mapred_jaas_file}}  -Djavax.security.auth.useSubjectCredsOnly=false"
{% endif %}

#export HADOOP_JHS_LOGGER=INFO,RFA # Hadoop JobSummary logger.
#export HADOOP_IDENT_STRING= #A string representing this instance of hadoop. $USER by default
#export HADOOP_NICENESS= #The scheduling priority for daemons. Defaults to 0.
export HADOOP_OPTS="-Dhdp.version=$HDP_VERSION $HADOOP_OPTS"
export HADOOP_OPTS="-Djava.io.tmpdir={{hadoop_java_io_tmpdir}} $HADOOP_OPTS"
export JAVA_LIBRARY_PATH="${JAVA_LIBRARY_PATH}:{{hadoop_java_io_tmpdir}}"

# History server logs
export HADOOP_LOG_DIR={{mapred_log_dir_prefix}}/$USER

# History server pid
export HADOOP_PID_DIR={{mapred_pid_dir_prefix}}/$USER

# Tez specific configuration
export TEZ_CONF_DIR={{config_dir}}

# Set HADOOP_HOME to point to a specific hadoop install directory
export HADOOP_HOME=${HADOOP_HOME:-{{hadoop_home}}}

# The java implementation to use.
export JAVA_HOME={{java64_home}}

# Set Hadoop-specific environment variables here.

#Set path to where bin/hadoop is available
#Set path to where bin/hadoop is available
export HADOOP_HOME=${HADOOP_HOME:-{{hadoop_home}}}

#set the path to where bin/hbase is available
export HBASE_HOME=${HBASE_HOME:-{{hbase_home}}}

#Set the path to where bin/hive is available
export HIVE_HOME=${HIVE_HOME:-{{hive_home}}}

#Set the path for where zookeper config dir is
export ZOOCFGDIR=${ZOOCFGDIR:-/etc/zookeeper/conf}

# add libthrift in hive to sqoop class path first so hive imports work
export SQOOP_USER_CLASSPATH="`ls ${HIVE_HOME}/lib/libthrift-*.jar 2> /dev/null`:${SQOOP_USER_CLASSPATH}"

export JAVA_HOME={{java64_home}}
export ZOOKEEPER_HOME={{zk_home}}
export ZOO_LOG_DIR={{zk_log_dir}}
export ZOOPIDFILE={{zk_pid_file}}
export SERVER_JVMFLAGS={{zk_server_heapsize}}
export JAVA=$JAVA_HOME/bin/java
export CLASSPATH=$CLASSPATH:/usr/share/zookeeper/*

{% if security_enabled %}
export SERVER_JVMFLAGS="$SERVER_JVMFLAGS -Djava.security.auth.login.config={{zk_server_jaas_file}}"
export CLIENT_JVMFLAGS="$CLIENT_JVMFLAGS -Djava.security.auth.login.config={{zk_client_jaas_file}} -Dzookeeper.sasl.client.username={{zk_principal_user}}"
{% endif %}

# Set environment variables here.

# AMS instance name
export AMS_INSTANCE_NAME={{hostname}}

# The java implementation to use. Java 1.6 required.
export JAVA_HOME={{java64_home}}

# Collector Log directory for log4j
export AMS_COLLECTOR_LOG_DIR={{ams_collector_log_dir}}

# Monitor Log directory for outfile
export AMS_MONITOR_LOG_DIR={{ams_monitor_log_dir}}

# Collector pid directory
export AMS_COLLECTOR_PID_DIR={{ams_collector_pid_dir}}

# Monitor pid directory
export AMS_MONITOR_PID_DIR={{ams_monitor_pid_dir}}

# AMS HBase pid directory
export AMS_HBASE_PID_DIR={{hbase_pid_dir}}

# AMS Collector heapsize
export AMS_COLLECTOR_HEAPSIZE={{metrics_collector_heapsize}}

# HBase Tables Initialization check enabled
export AMS_HBASE_INIT_CHECK_ENABLED={{ams_hbase_init_check_enabled}}

# AMS Collector options
export AMS_COLLECTOR_OPTS="-Djava.library.path=/usr/lib/ams-hbase/lib/hadoop-native"
{% if security_enabled %}
export AMS_COLLECTOR_OPTS="$AMS_COLLECTOR_OPTS -Dzookeeper.sasl.client.username={{zk_principal_user}} -Djava.security.auth.login.config={{ams_collector_jaas_config_file}}"
{% endif %}

# AMS Collector GC options
export AMS_COLLECTOR_GC_OPTS="-XX:+UseConcMarkSweepGC -verbose:gc -XX:+PrintGCDetails -XX:+PrintGCDateStamps -Xloggc:{{ams_collector_log_dir}}/collector-gc.log-`date +'%Y%m%d%H%M'`"
export AMS_COLLECTOR_OPTS="$AMS_COLLECTOR_OPTS $AMS_COLLECTOR_GC_OPTS"

# Metrics collector host will be blacklisted for specified number of seconds if metric monitor failed to connect to it.
export AMS_FAILOVER_STRATEGY_BLACKLISTED_INTERVAL={{failover_strategy_blacklisted_interval}}

# Extra Java CLASSPATH elements for Metrics Collector. Optional.
export COLLECTOR_ADDITIONAL_CLASSPATH={{ams_classpath_additional}}

# Set environment variables here.

# AMS UI Server Home Dir
export AMS_GRAFANA_HOME_DIR={{ams_grafana_home_dir}}

# AMS UI Server Data Dir
export AMS_GRAFANA_DATA_DIR={{ams_grafana_data_dir}}

# AMS UI Server Log Dir
export AMS_GRAFANA_LOG_DIR={{ams_grafana_log_dir}}

# AMS UI Server PID Dir
export AMS_GRAFANA_PID_DIR={{ams_grafana_pid_dir}}

# The java implementation to use. If JAVA_HOME is not found we expect java and jar to be in path
export JAVA_HOME={{java64_home}}

# any additional java opts you want to set. This will apply to both client and server operations
{% if security_enabled %}
export ATLAS_OPTS="{{metadata_opts}} -Dzookeeper.sasl.client.username={{zk_principal_user}} -Djava.security.auth.login.config={{atlas_jaas_file}}"
{% else %}
export ATLAS_OPTS="{{metadata_opts}}"
{% endif %}

# metadata configuration directory
export ATLAS_CONF={{conf_dir}}

# Where log files are stored. Defatult is logs directory under the base install location
export ATLAS_LOG_DIR={{log_dir}}

# additional classpath entries
export ATLASCPPATH={{metadata_classpath}}

# data dir
export ATLAS_DATA_DIR={{data_dir}}

# pid dir
export ATLAS_PID_DIR={{pid_dir}}

# hbase conf dir
export HBASE_CONF_DIR={{hbase_conf_dir}}

# Where do you want to expand the war file. By Default it is in /server/webapp dir under the base install dir.
export ATLAS_EXPANDED_WEBAPP_DIR={{expanded_war_dir}}
export ATLAS_SERVER_OPTS="-server -XX:SoftRefLRUPolicyMSPerMB=0 -XX:+CMSClassUnloadingEnabled -XX:+UseConcMarkSweepGC -XX:+CMSParallelRemarkEnabled -XX:+PrintTenuringDistribution -XX:+HeapDumpOnOutOfMemoryError -XX:HeapDumpPath=$ATLAS_LOG_DIR/atlas_server.hprof -Xloggc:$ATLAS_LOG_DIR/gc-worker.log -verbose:gc -XX:+UseGCLogFileRotation -XX:NumberOfGCLogFiles=10 -XX:GCLogFileSize=1m -XX:+PrintGCDetails -XX:+PrintHeapAtGC -XX:+PrintGCTimeStamps"
{% if java_version == 8 %}
export ATLAS_SERVER_HEAP="-Xms{{atlas_server_xmx}}m -Xmx{{atlas_server_xmx}}m -XX:MaxNewSize={{atlas_server_max_new_size}}m -XX:MetaspaceSize=100m -XX:MaxMetaspaceSize=512m"
{% else %}
export ATLAS_SERVER_HEAP="-Xms{{atlas_server_xmx}}m -Xmx{{atlas_server_xmx}}m -XX:MaxNewSize={{atlas_server_max_new_size}}m -XX:MaxPermSize=512m"
{% endif %}

#!/bin/bash

# Set KAFKA specific environment variables here.

# The java implementation to use.
export JAVA_HOME={{java64_home}}
export PATH=$PATH:$JAVA_HOME/bin
export PID_DIR={{kafka_pid_dir}}
export LOG_DIR={{kafka_log_dir}}
{% if kerberos_security_enabled or kafka_other_sasl_enabled %}
export KAFKA_KERBEROS_PARAMS="-Djavax.security.auth.useSubjectCredsOnly=false {{kafka_kerberos_params}}"
{% else %}
export KAFKA_KERBEROS_PARAMS={{kafka_kerberos_params}}
{% endif %}
# Add kafka sink to classpath and related depenencies
if [ -e "/usr/lib/ambari-metrics-kafka-sink/ambari-metrics-kafka-sink.jar" ]; then
  export CLASSPATH=$CLASSPATH:/usr/lib/ambari-metrics-kafka-sink/ambari-metrics-kafka-sink.jar
  export CLASSPATH=$CLASSPATH:/usr/lib/ambari-metrics-kafka-sink/lib/*
fi
if [ -f /etc/kafka/conf/kafka-ranger-env.sh ]; then
. /etc/kafka/conf/kafka-ranger-env.sh
fi

{% if kerberos_security_enabled %}
KafkaClient {
com.sun.security.auth.module.Krb5LoginModule required
useTicketCache=true
renewTicket=true
serviceName="{{kafka_bare_jaas_principal}}";
};
{% endif %}

/**
* Example of SASL/PLAIN Configuration
*
* KafkaServer {
*   org.apache.kafka.common.security.plain.PlainLoginModule required
*   username="admin"
*   password="admin-secret"
*   user_admin="admin-secret"
*   user_alice="alice-secret";
*   };
*
* Example of SASL/SCRAM
*
* KafkaServer {
*   org.apache.kafka.common.security.scram.ScramLoginModule required
*   username="admin"
*   password="admin-secret"
*   };
*
* Example of Enabling multiple SASL mechanisms in a broker:
*
*   KafkaServer {
*
*    com.sun.security.auth.module.Krb5LoginModule required
*    useKeyTab=true
*    storeKey=true
*    keyTab="/etc/security/keytabs/kafka_server.keytab"
*    principal="kafka/kafka1.hostname.com@EXAMPLE.COM";
*
*    org.apache.kafka.common.security.plain.PlainLoginModule required
*    username="admin"
*    password="admin-secret"
*    user_admin="admin-secret"
*    user_alice="alice-secret";
*
*    org.apache.kafka.common.security.scram.ScramLoginModule required
*    username="scram-admin"
*    password="scram-admin-secret";
*    };
*
**/

{% if kerberos_security_enabled %}

KafkaServer {
com.sun.security.auth.module.Krb5LoginModule required
useKeyTab=true
keyTab="{{kafka_keytab_path}}"
storeKey=true
useTicketCache=false
serviceName="{{kafka_bare_jaas_principal}}"
principal="{{kafka_jaas_principal}}";
};
KafkaClient {
com.sun.security.auth.module.Krb5LoginModule required
useTicketCache=true
renewTicket=true
serviceName="{{kafka_bare_jaas_principal}}";
};
Client {
com.sun.security.auth.module.Krb5LoginModule required
useKeyTab=true
keyTab="{{kafka_keytab_path}}"
storeKey=true
useTicketCache=false
serviceName="zookeeper"
principal="{{kafka_jaas_principal}}";
};
com.sun.security.jgss.krb5.initiate {
com.sun.security.auth.module.Krb5LoginModule required
renewTGT=false
doNotPrompt=true
useKeyTab=true
keyTab="{{kafka_keytab_path}}"
storeKey=true
useTicketCache=false
serviceName="{{kafka_bare_jaas_principal}}"
principal="{{kafka_jaas_principal}}";
};

{% endif %}

